mapper.select:
select t.id,u1.name as owner,t.money,t.name,t.expected_date,c1.name as customer_id,d1.value as stage,d2.value as type, d3.value as source,a.name as activity_id,ct.fullname as contacts_id,u2.name as create_by, t.create_time,u3.name as edit_by,t.edit_time,t.description,t.contact_summary,t.next_contact_time from tbl_transaction t join tbl_user u1 on u1.id = t.owner join tbl_customer c1 on c1.id = t.customer_id join tbl_dic_value d1 on d1.id = t.stage left join tbl_dic_value d2 on d2.id = t.type left join tbl_dic_value d3 on d3.id = t.source left join tbl_activity a on a.id = t.activity_id left join tbl_contacts ct on ct.id = t.contacts_id join tbl_user u2 on u2.id = t.create_by left join tbl_user u3 on u3.id = t.edit_by
and u1.name like concat('%',#{owner},'%')
and t.name like concat('%',#{name},'%')
and c1.name like concat('%',#{customerId},'%')
and d1.value = #{stage}
and d2.value = #{type}
and d3.value = #{source}
and ct.fullname like concat('%',#{contactsId},'%')
order by t.create_time desc limit #{beginNo},#{pageSize}

select count(*) from tbl_transaction t join tbl_user u1 on u1.id = t.owner join tbl_customer c1 on c1.id = t.customer_id join tbl_dic_value d1 on d1.id = t.stage left join tbl_dic_value d2 on d2.id = t.type left join tbl_dic_value d3 on d3.id = t.source left join tbl_activity a on a.id = t.activity_id left join tbl_contacts ct on ct.id = t.contacts_id join tbl_user u2 on u2.id = t.create_by left join tbl_user u3 on u3.id = t.edit_by
and u1.name like concat('%',#{owner},'%')
and t.name like concat('%',#{name},'%')
and c1.name like concat('%',#{customerId},'%')
and d1.value = #{stage}
and d2.value = #{type}
and d3.value = #{source}
and ct.fullname like concat('%',#{contactsId},'%')
